DROP TRIGGER IF EXISTS `t1`;
DELIMITER ;;
CREATE TRIGGER `t1` AFTER INSERT ON `art` FOR EACH ROW begin
update cat set art_num=art_num+1 where cat_id=new.catid;
end
;;
DELIMITER ;
DROP TRIGGER IF EXISTS `t2`;
DELIMITER ;;
CREATE TRIGGER `t2` AFTER UPDATE ON `art` FOR EACH ROW begin
update cat set art_num=art_num+1 where cat_id=new.catid;
update cat set art_num=art_num-1 where cat_id=old.catid;
end
;;
DELIMITER ;
